import * as React from "react";
import {
  View,
  ScrollView,
  Text,
  StyleSheet,
  Image,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import { StackScreenProps } from "@react-navigation/stack";
import { PlaylistDetail, Item } from "../models/PlaylistDetail";
import { Audio } from "expo-av";

const API_ENDPOINT = "http://localhost:3001";

const soundObject = new Audio.Sound();

type RootStackParamList = {
  PlaylistDetail: { id: string };
};

type Props = StackScreenProps<RootStackParamList, "PlaylistDetail">;

export default function PlaylistDetailScreen({ route }: Props) {
  const [playlistDetail, setPlaylistDetail] = React.useState<PlaylistDetail>();
  const { id } = route.params;

  React.useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`${API_ENDPOINT}/v1/playlists/${id}`);
      // console.log("response.data[0] : ", response.data);
      setPlaylistDetail(response.data);
    };
    fetchData();
  });

  const handleSound = async (sound: string) => {
    console.log("sound : ", sound);
    try {
      soundObject.setOnPlaybackStatusUpdate(onPlaybackStatusUpdate);
      await soundObject.loadAsync(source, initialStatus, downloadFirst);
      await soundObject.loadAsync(sound);
      await soundObject.playAsync();
      // Your sound is playing!

      // Don't forget to unload the sound from memory
      // when you are done using the Sound object
      await soundObject.unloadAsync();
    } catch (error) {
      // An error occurred!
    }
  };

  return (
    <View style={styles.container}>
      {playlistDetail && (
        <View>
          <View>
            <Image
              source={{ uri: playlistDetail.images[0].url }}
              style={{ width: 85, height: 85 }}
            />
            <Text style={styles.title}>{playlistDetail.name}</Text>
            <Text style={styles.description}>{playlistDetail.description}</Text>
          </View>
          <ScrollView>
            {playlistDetail.tracks.items.map((item: Item) => (
              <TouchableOpacity
                key={item.track.id}
                onPress={() => handleSound(item.track.preview_url)}
              >
                <Text
                  style={{ color: item.track.preview_url ? "#000" : "grey" }}
                >
                  {item.track.name}
                </Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  description: {
    fontSize: 12,
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
});
