import * as React from "react";
import {
  View,
  ScrollView,
  Text,
  StyleSheet,
  Image,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import { StackScreenProps } from "@react-navigation/stack";
import { PlaylistDetail, Item, Track } from "../models/PlaylistDetail";
import { Audio } from "expo-av";

const API_ENDPOINT = "http://localhost:3001";

type RootStackParamList = {
  PlaylistDetail: { id: string };
};

type Props = StackScreenProps<RootStackParamList, "PlaylistDetail">;

export default function PlaylistDetailScreen({ route }: Props) {
  const [playlistDetail, setPlaylistDetail] = React.useState<PlaylistDetail>();
  const [track, setTrack] = React.useState<Track>();
  const [audioState, setAudioState] = React.useState({
    isPlaying: false,
    playbackInstance: null,
    currentIndex: 0,
    volume: 1.0,
    isBuffering: false,
  });
  const { id } = route.params;

  React.useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`${API_ENDPOINT}/v1/playlists/${id}`);
      setPlaylistDetail(response.data);
    };
    fetchData();
  });

  const onPlaybackStatusUpdate = (status: any) => {
    // console.log("status : ", status);
    setAudioState({
      ...audioState,
      isBuffering: status.isBuffering,
    });
  };

  const loadAudio = async (soundUri: string) => {
    if (!soundUri) return;

    const playbackInstance = new Audio.Sound();
    console.log("playbackInstance : ", playbackInstance);
    const status = await playbackInstance.getStatusAsync();
    console.log("status : ", status);

    try {
      const source = {
        uri: soundUri,
      };

      const status = {
        shouldPlay: true,
        volume: audioState.volume,
      };

      playbackInstance.setOnPlaybackStatusUpdate(onPlaybackStatusUpdate);
      await playbackInstance.loadAsync(source, status, false);
      setAudioState({ ...audioState, playbackInstance, isPlaying: true });
    } catch (e) {
      console.log(e);
    }
  };

  const handlePlayPause = async () => {
    setAudioState({
      ...audioState,
      isPlaying: !audioState.isPlaying,
    });
  };

  return (
    <View style={styles.container}>
      {playlistDetail && (
        <View>
          {/*<View>*/}
          {/*  <Image*/}
          {/*    source={{ uri: playlistDetail.images[0].url }}*/}
          {/*    style={{ width: 85, height: 85 }}*/}
          {/*  />*/}
          {/*  <Text style={styles.title}>{playlistDetail.name}</Text>*/}
          {/*  <Text style={styles.description}>{playlistDetail.description}</Text>*/}
          {/*</View>*/}
          <ScrollView style={styles.body}>
            {playlistDetail.tracks.items.map((item: Item) => (
              <TouchableOpacity
                key={item.track.id}
                onPress={() => {
                  setTrack(item.track);
                  loadAudio(item.track.preview_url);
                }}
              >
                <Text
                  style={{ color: item.track.preview_url ? "#000" : "grey" }}
                >
                  {item.track.name}
                </Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>
      )}
      {track && (
        <View style={styles.player}>
          <Text style={styles.playerText}>{track?.name}</Text>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // justifyContent: "center",
    // alignItem: "flex-start",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  description: {
    fontSize: 12,
  },
  body: {
    height: "80vh",
    overflow: "hidden",
  },
  player: {
    // flex: 1,
    width: "100%",
    height: 90,
    backgroundColor: "#000",
    position: "absolute",
    bottom: 0,
  },
  playerText: {
    color: "#fff",
  },
});
