import * as React from "react";
import { View, ScrollView, Text, StyleSheet } from "react-native";
import axios from "axios";

const API_ENDPOINT = "http://localhost:3001";


export default function PlaylistDetailScreen({ route, navigation }) {
  const [playlistDetail, setPlaylistDetail] = React.useState();
  const { id } = route.params;

  React.useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `${API_ENDPOINT}/v1/browse/featured-playlists/${id}`
      );
      setPlaylistDetail(response.data[0]);
    };
    fetchData();
  });

  return (
    <View style={styles.container}>
      {playlistDetail && (<ScrollView >
          <Text style={styles.title}>PREVIEW SCREEN</Text>
          <View
            style={styles.separator}
            lightColor="#eee"
            darkColor="rgba(255,255,255,0.1)"
          />
        </ScrollView>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
});
