import * as React from "react";
import {
  View,
  ScrollView,
  Text,
  StyleSheet,
  Image,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import { StackScreenProps } from "@react-navigation/stack";
import { PlaylistDetail, Item } from "../models/PlaylistDetail";
import { Audio } from "expo-av";

const API_ENDPOINT = "http://localhost:3001";

//function to initialize the Track Player
const trackPlayerInit = async () => {
  await TrackPlayer.setupPlayer();
  await TrackPlayer.add({
    id: "1",
    url:
      "https://audio-previews.elements.envatousercontent.com/files/103682271/preview.mp3",
    type: "default",
    title: "My Title",
    album: "My Album",
    artist: "Rohan Bhatia",
    artwork: "https://picsum.photos/100",
  });
  return true;
};

type RootStackParamList = {
  PlaylistDetail: { id: string };
};

type Props = StackScreenProps<RootStackParamList, "PlaylistDetail">;

export default function PlaylistDetailScreen({ route }: Props) {
  const [playlistDetail, setPlaylistDetail] = React.useState<PlaylistDetail>();
  const { id } = route.params;

  React.useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(`${API_ENDPOINT}/v1/playlists/${id}`);
      // console.log("response.data[0] : ", response.data);
      setPlaylistDetail(response.data);
    };
    fetchData();
  });

  const handleSound = (sound: string) => {
    console.log("sound : ", sound);
  };

  return (
    <View style={styles.container}>
      {playlistDetail && (
        <View>
          <View>
            <Image
              source={{ uri: playlistDetail.images[0].url }}
              style={{ width: 85, height: 85 }}
            />
            <Text style={styles.title}>{playlistDetail.name}</Text>
            <Text style={styles.description}>{playlistDetail.description}</Text>
          </View>
          <ScrollView>
            {playlistDetail.tracks.items.map((item: Item) => (
              <TouchableOpacity
                key={item.track.id}
                onPress={() => handleSound(item.track.preview_url)}
              >
                <Text
                  style={{ color: item.track.preview_url ? "#000" : "grey" }}
                >
                  {item.track.name}
                </Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  description: {
    fontSize: 12,
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
});
