import * as React from "react";
import {
  View,
  Text,
  SafeAreaView,
  FlatList,
  StyleSheet,
  Image,
  ScrollView,
  TouchableOpacity,
} from "react-native";
import axios from "axios";
import { StackScreenProps } from "@react-navigation/stack";

const API_ENDPOINT = "http://localhost:3001";

type RootStackParamList = {
  Playlists: undefined;
  PlaylistDetail: { id: string };
};

type Props = StackScreenProps<RootStackParamList, "Playlists">;

export default function PlaylistsScreen({ navigation }: Props) {
  const [playlists, setPlaylists] = React.useState();
  const [selectedId, setSelectedId] = React.useState(null);

  React.useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        `${API_ENDPOINT}/v1/browse/featured-playlists`
      );
      setPlaylists(response.data.playlists.items);
    };
    fetchData();
  });

  // console.log("playlists : ", playlists);

  const renderItem = ({ item }: any) => {
    const backgroundColor = item.id === selectedId ? "#6e3b6e" : "#f9c2ff";
    return (
      <TouchableOpacity
        onPress={() => {
          setSelectedId(item.id);
          navigation.navigate("PlaylistDetail", { id: item.id });
        }}
      >
        <Image
          source={{ uri: item.images[0].url }}
          style={{ width: 400, height: 400 }}
        />
        {/*<View*/}
        {/*  style={[*/}
        {/*    styles.item,*/}
        {/*    {*/}
        {/*      backgroundColor,*/}
        {/*      backgroundImage: item.images[0]*/}
        {/*        ? `url(${item.images[0].url})`*/}
        {/*        : "",*/}
        {/*    },*/}
        {/*  ]}*/}
        {/*>*/}
        {/*  <Text style={styles.title}>{item.title}</Text>*/}
        {/*</View>*/}
      </TouchableOpacity>
      // <Item
      //   item={item}
      //   onPress={() => {
      //     setSelectedId(item.id);
      //     navigation.navigate("PlaylistDetail", { id: item.id });
      //   }}
      //   style={{
      //     backgroundColor,
      //     backgroundImage: `url(${item.images[0].url})`,
      //   }}
      // />
    );
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Editor's picks</Text>
      <SafeAreaView style={styles.container}>
        <ScrollView style={styles.flatContainer}>
          <FlatList
            data={playlists}
            renderItem={renderItem}
            keyExtractor={(item) => item.id}
            extraData={selectedId}
          />
        </ScrollView>
      </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  flatContainer: {
    display: "flex",
    flexDirection: "row",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
  item: {
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
    width: 170,
    height: 170,
    backgroundPosition: "center",
    backgroundSize: "contain",
  },
});
